mapper.update:
update display
group_id = #{groupId,jdbcType=INTEGER}, display_name = #{displayName,jdbcType=VARCHAR}, display_context = #{displayContext,jdbcType=LONGVARCHAR}, machine_code = #{machineCode,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP},
where id=#{id}

mapper.select:
		select
		id,
		group_id,
		display_name,
		display_context,
		machine_code,
		status,
		create_time
		from display

		 and display.id = #{id,jdbcType=INTEGER}
		 and display.group_id = #{groupId,jdbcType=INTEGER}
		 and display.display_name = #{displayName,jdbcType=VARCHAR}
		 and display.display_context = #{displayContext,jdbcType=LONGVARCHAR}
		 and display.machine_code = #{machineCode,jdbcType=VARCHAR}
		 and display.status = #{status,jdbcType=INTEGER}
		 and display.create_time = #{createTime,jdbcType=TIMESTAMP}
		 and display.create_time BETWEEN #{startDate} and #{endDate}



              ${orderByString}


              order by id desc

